function showElements(){const n=document.getElementsByClassName("init-anim");animateElements(n,"show")}function showFooter(){const n=window.innerHeight+window.scrollY,e=document.querySelector(".footer-anim"),o=e.offsetTop;n>=o&&addClass(e,"show")}function animateElements(n,e){Array.prototype.forEach.call(n,n=>{const o=n.getBoundingClientRect().top;window.innerHeight/6*5>=o&&addClass(n,e)})}function addClass(n,e){const o=n.className.split(" ");-1===o.indexOf(e)&&(n.className+=" "+e)}document.addEventListener("scroll",function(){showElements(),showFooter()}),window.addEventListener("load",function(){showElements(),showFooter()});